clean<-function(df){
  
  data<-data.frame(doc_id=df$id,
                   text=df$content, stringsAsFactors = FALSE)
  
  # data$doc_id<-str_replace(data$doc_id, "(.*)-.*$", "\\1")
  # 
  # data %>% 
  #   group_by(doc_id) %>% 
  #   mutate(bars_by_foo = paste0(bar, collapse = ""))
  
  corpalltxt <- Corpus(DataframeSource(data))
  #corpalltxt  <-Corpus(DirSource(dest, recursive = TRUE), readerControl = list(reader=readPlain)) #specifies the exact folder where my text file(s) is for analysis with tm.
  show(corpalltxt)
  
  
  corpusclean <- tm_map(corpalltxt, removeNumbers)
  corpusclean <- tm_map(corpusclean, removePunctuation)
  grep('^[A-Z]', unlist(strsplit(words, ' ')), value = T)
  #Delete month names
  corpusclean <- tm_map(corpusclean, removeWords, c("January","February","March","April","May","June","July","August","September","October","November","December"))
  corpusclean <- tm_map(corpusclean, content_transformer(tolower))
  corpusclean <- tm_map(corpusclean, removeWords, c("film", "movie", "movies", "films", "much", "one", "two", "three", "also"))
  corpusclean <- tm_map(corpusclean, removeWords, stopwords("english")) # this stopword file is at C:\Users\[username]\Documents\R\win-library\2.13\tm\stopwords 
  corpusclean <- tm_map(corpusclean, stripWhitespace)

  return(corpusclean)}
